local function FetchServers() SBRefresh.Text = "..." for _, c in ipairs(SBList:GetChildren()) do if c:IsA("Frame") then c:Destroy() end end task.spawn(function() local s, r = pcall(function() return request({Url = "https://games.roblox.com/v1/games/"..game.PlaceId.."/servers/Public?sortOrder=Asc&limit=100", Method = "GET"}) end) if s and r and r.Body then local d = HttpService:JSONDecode(r.Body) if d and d.data then local srvs = d.data table.sort(srvs, function(a,b) return (ServerSortMode=="Smallest" and (a.playing or 0) < (b.playing or 0)) or ((a.playing or 0) > (b.playing or 0)) end) for _, srv in ipairs(srvs) do if srv.playing and srv.id ~= game.JobId then local row = Instance.new("Frame", SBList) row.Size, row.BackgroundColor3 = UDim2.new(1, -6, 0, 45), THEME.ItemBG Instance.new("UICorner", row).CornerRadius = UDim.new(0, 6) local info = Instance.new("TextLabel", row) info.Text, info.Size, info.Position, info.BackgroundTransparency, info.TextColor3, info.Font, info.TextSize, info.TextXAlignment = "Players: "..srv.playing.." / "..srv.maxPlayers.."\nPing: "..(srv.ping or "?").."ms", UDim2.new(0.6, 0, 1, 0), UDim2.new(0, 10, 0, 0), 1, THEME.Text, Enum.Font.Gotham, 12, Enum.TextXAlignment.Left local join = Instance.new("TextButton", row) join.Text, join.BackgroundColor3, join.Size, join.Position, join.Font, join.TextSize, join.TextColor3 = "Join", Color3.fromRGB(100, 200, 100), UDim2.new(0, 80, 0, 25), UDim2.new(1, -90, 0.5, -12.5), Enum.Font.GothamBold, 12, Color3.fromRGB(255,255,255) Instance.new("UICorner", join).CornerRadius = UDim.new(0, 4) join.MouseButton1Click:Connect(function() join.Text = "Joining..." if queue_on_teleport then queue_on_teleport('loadstring(game:HttpGet("https://raw.githubusercontent.com/skrylor/Last-Letter-Script/refs/heads/main/Last%20Letter.lua"))()') end pcall(function() game:GetService("TeleportService"):TeleportToPlaceInstance(game.PlaceId, srv.id, Players.LocalPlayer) end) end) end end end SBList.CanvasSize = UDim2.new(0,0,0, SBLayout.AbsoluteContentSize.Y) end SBRefresh.Text = "Refresh" end) end
