local KEY_POS = {} local r1, r2, r3 = "qwertyuiop", "asdfghjkl", "zxcvbnm" for i=1,#r1 do KEY_POS[r1:sub(i,i)] = {x=i, y=1} end for i=1,#r2 do KEY_POS[r2:sub(i,i)] = {x=i+0.5, y=2} end for i=1,#r3 do KEY_POS[r3:sub(i,i)] = {x=i+1, y=3} end
local function KeyDistance(a, b) if not a or not b then return 1 end a, b = a:lower(), b:lower() local pa, pb = KEY_POS[a], KEY_POS[b] return pa and pb and math.sqrt((pa.x-pb.x)^2 + (pa.y-pb.y)^2) or 1 end
local function CalculateDelayForKeys(pC, nC) if isBlatant then return 60/currentCPM end local bD = 60/currentCPM local var, extra = bD*0.35, 0 if useHumanization and useFingerModel and pC and nC and pC~="" then local d = KeyDistance(pC, nC) extra = d*0.018*(550/math.max(150, currentCPM)) local pa, pb = KEY_POS[pC:lower()], KEY_POS[nC:lower()] if pa and pb and ((pa.x<=5 and pb.x<=5) or (pa.x>5 and pb.x>5)) then extra = extra*0.8 end end if useHumanization then return math.max(0.005, bD + extra + ((math.random()+math.random()+math.random())/3*2-1)*var) else return bD end end
