local function GetKeyCode(c) local l = Config.KeyboardLayout or "QWERTY" if type(c)=="string" and #c==1 then c = c:lower() if l=="QWERTZ" and c=="z" then return Enum.KeyCode.Y elseif l=="QWERTZ" and c=="y" then return Enum.KeyCode.Z end return Enum.KeyCode[c:upper()] end end
local function SimulateKey(i) if typeof(i)=="string" and #i==1 then if not pcall(function() VirtualInputManager:SendTextInput(i) end) then local k = GetKeyCode(i) if k then VirtualInputManager:SendKeyEvent(true, k, false, game) task.wait(0.01) VirtualInputManager:SendKeyEvent(false, k, false, game) end end else local k = typeof(i)=="EnumItem" and i or Enum.KeyCode[i:upper()] if k then VirtualInputManager:SendKeyEvent(true, k, false, game) task.wait(0.01) VirtualInputManager:SendKeyEvent(false, k, false, game) end end end
local function Backspace(c) local f = UserInputService:GetFocusedTextBox() if f and f.TextEditable then f.Text = f.Text:sub(1, -c-1) return end for i=1,c do SimulateKey(Enum.KeyCode.Backspace) end end
SmartType = function(tW, cD, isC, bP) if unloaded or isTyping then return end isTyping, lastTypingStart = true, tick() local tb = (Players.LocalPlayer and Players.LocalPlayer:FindFirstChild("PlayerGui") and Players.LocalPlayer.PlayerGui:FindFirstChild("InGame") and Players.LocalPlayer.PlayerGui.InGame:FindFirstChild("Frame"):FindFirstChildWhichIsA("TextBox")) or UserInputService:GetFocusedTextBox() if tb then tb:CaptureFocus() end pcall(function() local mP = tW:sub(1,#cD)==cD and tW:sub(#cD+1) or tW for i=1,#mP do if not bP and not GetTurnInfo() then break end local ch = mP:sub(i,i) SimulateKey(ch) task.wait(60/currentCPM) end SimulateKey(Enum.KeyCode.Return) task.wait(0.2) if GetCurrentGameWord()~="" then UsedWords[tW] = true end end) isTyping = false end
UpdateList = function(dT, rL) local matches, sP = {}, SearchBox.Text:lower():gsub("[%s%c]+", "") if sP=="" then sP = dT end local fC = sP:sub(1,1) if fC=="" or fC=="#" then fC = rL and rL:sub(1,1):lower() or "#" end for _, w in ipairs(Buckets[fC] or Words) do if not UsedWords[w] and w:sub(1,#sP)==sP then table.insert(matches, w) if #matches>=40 then break end end end for i=1, math.max(40, #ButtonCache) do local w, btn = matches[i], ButtonCache[i] if w then if not btn then btn = Instance.new("TextButton", ScrollList) btn.Size, btn.BackgroundColor3, btn.Text = UDim2.new(1, -6, 0, 30), THEME.ItemBG, "" local lbl = Instance.new("TextLabel", btn) lbl.Name, lbl.Size, lbl.BackgroundTransparency, lbl.Font, lbl.TextColor3, lbl.RichText = "Label", UDim2.new(1,0,1,0), 1, Enum.Font.GothamMedium, THEME.Text, true btn.MouseButton1Click:Connect(function() SmartType(w, dT, true) end) table.insert(ButtonCache, btn) end btn.Visible, btn.Label.Text = true, "<font color=\"rgb("..ColorToRGB(THEME.Accent)..")\">"..w:sub(1,#sP).."</font>"..w:sub(#sP+1) else if btn then btn.Visible = false end end end ScrollList.CanvasSize = UDim2.new(0,0,0, UIListLayout.AbsoluteContentSize.Y) end
