RunService.RenderStepped:Connect(function() if unloaded then return end local n, p = tick(), Players.LocalPlayer local g = p and p:FindFirstChild("PlayerGui") local f = g and g:FindFirstChild("InGame") and g.InGame:FindFirstChild("Frame") local vis = f and f.Parent and (f.Parent:IsA("ScreenGui") and f.Parent.Enabled or f.Parent.Visible) local secs = nil if vis then local cir = f:FindFirstChild("Circle") local tL = cir and cir:FindFirstChild("Timer") and cir.Timer:FindFirstChild("Seconds") if tL then secs = tonumber(tL.Text:match("([%d%.]+)")) st.Text = tL.Text if secs and secs ~= lastTimerVal then lastTimerVal, lastTimerTick = secs, n end end end if n - lastTimerTick > 2 and next(UsedWords) then ResetState() end local imT, rL = GetTurnInfo(f) local det = GetCurrentGameWord(f) if det ~= lastDetected or rL ~= lastRequiredLetter or forceUpdateList then lastDetected, lastRequiredLetter, forceUpdateList = det, rL, false UpdateList(det, rL) StatusText.Text = (det=="" and "Waiting..." or "Input: "..det) end if autoPlay and not isTyping and imT and det==lastDetected and #det>=0 then task.spawn(function() task.wait(0.5) if GetTurnInfo() then local match = matches and matches[1] if match then SmartType(match, det, false) end end end) end end)
inputConn = UserInputService.InputBegan:Connect(function(i) if not unloaded and i.KeyCode == TOGGLE_KEY then ScreenGui.Enabled = not ScreenGui.Enabled end end)
